const modalArea = document.getElementById("modalArea"); function showModal(a, d, l) { modalArea.innerHTML = `\n    <div>\n    <div class="modal fade show text-justify" id="modal${a}" tabindex="-1" role="dialog" aria-labelledby="exampleModalLongTitle" style="display: block; padding-right: 17px;">\n        <div class="modal-dialog" role="document">\n            <div class="modal-content">\n                <div class="modal-header">\n                    <h5 class="modal-title" id="exampleModalLongTitle">${d}</h5>\n                    <button type="button" class="close" data-dismiss="modal" aria-label="Close">\n                        <span aria-hidden="true">×</span>\n                    </button>\n                </div>\n                <div class="modal-body">\n                ${l}\n                </div>\n                <div class="modal-footer">\n                    <button type="button" class="main-btn" data-dismiss="modal">Close</button>\n                </div>\n            </div>\n        </div>\n    </div>\n</div>` };

function additionalModal(a, d, n, l, o) { modalArea.innerHTML = `\n    <div class="modal fade" id="addtional${a}" tabindex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">\n  <div class="modal-dialog modal-dialog-centered" role="document">\n    <div class="modal-content">\n      <div class="modal-header">\n        <h5 class="modal-title" id="exampleModalLongTitle">Contact Info of ${o}</h5>\n        <button type="button" class="close" data-dismiss="modal" aria-label="Close">\n          <span aria-hidden="true">&times;</span>\n        </button>\n      </div>\n      <div class="modal-body">\n      <p>Email   : ${d}</p>\n      <p>Phone   : ${n}</p>\n      <p>Contact : ${l}</p>\n      </div>\n      <div class="modal-footer">\n        <button type="button" class="main-btn" data-dismiss="modal">Close</button>\n      </div>\n    </div>\n  </div>\n</div>` };


